ScriptGroup.script:
function initialize_manager()
	local manager = {}

  manager.char_fields = {
    'name',
    'current_loadout',
    'container',
    'load_command',
  }

  manager.loadout_fields = {
    'name',
    'wield',
    'sheath',
    'stance'
  }

  manager.debug = false

	return manager
end

manager = manager or initialize_manager()

Script.script:
function manager.initialization.initialize_db()
	local mydb = db:create("char_info", {
    profile_info = {
      use_default_char = "false",
      default_char = "",
      _unique = { "default_char" },
			_violations = "FAIL"
    },
		char_vars = {
			name = "",
      description = "",
      current_loadout = "",
      load_command = "",
      container = "",
			_unique = { "name" },
			_violations = "FAIL"
		},
	})
  manager.initialization.create_tables("char_info")

  return mydb
end

function manager.initialization.create_tables(db_name_unsafe) db.debug_sql = true local sheets = { loadouts = [[ CREATE TABLE IF NOT EXISTS loadouts ( name TEXT NOT NULL, char_name TEXT NOT NULL, wield TEXT NOT NULL, sheath TEXT NULL, stance TEXT NOT NULL, PRIMARY KEY (name, char_name), FOREIGN KEY (char_name) REFERENCES char_vars (name) ON DELETE CASCADE ON UPDATE NO ACTION);]], loadout_actions = [[ CREATE TABLE IF NOT EXISTS loadout_actions ( loadout_name TEXT NOT NULL, char_name TEXT NOT NULL, name TEXT NOT NULL, value TEXT NOT NULL, PRIMARY KEY (loadout_name, char_name, name), FOREIGN KEY (char_name) REFERENCES char_vars (name) ON DELETE CASCADE ON UPDATE NO ACTION, FOREIGN KEY (loadout_name) REFERENCES loadout (name) ON DELETE CASCADE ON UPDATE NO ACTION);]] }
local conn = manager.db.get_conn(db_name_unsafe)
for name,sheet in pairs(sheets) do local r, err = conn:execute(sheet)
if r == nil then echo(f'# Unable to create table {name}: {err}\n') return end end
manager.db.commit(conn) end

function manager.initialization.get_profile_info(mydb)

	local profile_info = db:fetch(mydb.profile_info)

	if #profile_info == 0 then
		local r, err = db:add(mydb.profile_info, {
			use_default_char = "false",
      default_char = ""
		})

    if not r then
      echo(f'# error creating profile_info: {err}\n')
    end

		echo('\n# new profile_info created\n')

		profile_info = db:fetch(mydb.profile_info)
	end

  return profile_info[1]

end

function manager.initialization.get_char_vars(mydb, character)
local char_vars = db:fetch(mydb.char_vars, db:eq(mydb.char_vars.name, character))
if #char_vars == 0 then local r, err = db:add(mydb.char_vars, { name = character, current_loadout_type = nil, current_loadout_index = 0, container = nil, })
if not r then echo(f'# error creating char_vars: {err}\n') end
echo(f'\n# new char_vars "{character}" created\n')
char_vars = db:fetch(mydb.char_vars, db:eq(mydb.char_vars.name, character)) end
return char_vars[1]
end

function manager.char.set_default_character(character)
  local mydb = db:get_database("char_info")

	if not manager.char.character_exists(mydb, character) then
    echo(f'# No such character: {character}\n')
    return
  end

  db:set(mydb.profile_info.default_char, character)
  db:set(mydb.profile_info.use_default_char, "true")

  echo(f'\n# character "{character}" is now the default\n')
end

function manager.char.initialize(character) local mydb = db:get_database("char_info")
if not manager.char.character_exists(mydb, character) then echo(f'# No such character: {character}\n') return end
manager.initialization.initialize_character(mydb, character) end

function manager.char.add(character, description)
--  db.debug_sql = true
  local mydb = db:get_database("char_info")
  local r, err = db:add(mydb.char_vars, {
			name = character,
      description = description,
		})

    if not r then
      echo(f'# error creating char_vars: {err}\n')
    else
      echo(f'\n# new char_vars "{character}" created\n')
    end
end

function manager.char.delete(character)
  local mydb = db:get_database("char_info")
  db:delete(mydb.char_vars, db:eq(mydb.char_vars.name, character))

  echo(f'\n# character "{character}" deleted\n')
end

function manager.char.list() local mydb = db:get_database("char_info") local chars = db:fetch(mydb.char_vars)
if #chars == 0 then echo("# no chars found\n") return end
for k, v in pairs(chars) do echo(f'# character: {v.name} ({v.description})\n') end end

function manager.char.character_exists(mydb, character)
local char_vars = db:fetch(mydb.char_vars, db:eq(mydb.char_vars.name, character))
return #char_vars ~= 0 end

function manager.char.add_load_command(character, command)
  local mydb = db:get_database("char_info")

	if not manager.char.character_exists(mydb, character) then
    echo(f'# No such character: {character}\n')
    return
  end

  db:set(mydb.char_vars.load_command, command, db:eq(mydb.char_vars.name, character))

  echo(f'\n# character "{character}" now has load command "{command}"\n')
end

ScriptGroup.script:
function initialize_manager_loadouts()
	local loadouts = {}

  loadouts.current = {}

	return loadouts
end

manager.loadouts = manager.loadouts or initialize_manager_loadouts()

function initialize_manager_loadouts_actions()
	local actions = {}

  actions.current = {}

	return actions
end

manager.loadouts.actions = manager.loadouts.actions or initialize_manager_loadouts_actions()

Script.script:
function manager.loadouts.actions.list()
  echo(f'# Loadout: {manager.current_loadout}\n')

  local any_exist = manager.loadouts.actions.each(function(row) echo(f'#\taction: {row.name} = {row.value}\n') end)

  if not any_exist then
    echo("#\tnone\n")
  end
end

function manager.loadouts.actions.load()
  local current = {}

  manager.loadouts.actions.each(function(row) current[row.name] = row.value end)

  manager.loadouts.actions.current = current
end

function manager.db.commit(conn)
  local r, err = conn:commit()

  if r == nil then
    echo(f'# db commit failed: {err}\n')
  end
end

function manager.util.copy_table(tbl, fields) result = {}
for _, field in ipairs(fields) do result[field] = tbl[field] end
return result end

function manager.targetting.target_action(action)
	expandAlias(f'{action} {manager.targetting.target}')
end

ScriptGroup.script:
function initialize_manager_doors() local doors = {}
doors.use_mapper = true
doors.door_names = { ["north"] = "exit n", ["south"] = "exit s", ["east"] = "exit e", ["west"] = "exit w", ["up"] = "exit u", ["down"] = "exit d" }
return doors end
manager.doors = manager.doors or initialize_manager_doors()

Script.script:
function manager.doors.set_door_name(name, value)
	manager.doors.door_names[name] = value
end

function manager.doors.get_door_name(name)
	return manager.doors.door_names[name]
end

function manager.doors.open_door(name)
  manager.doors.act_door_name("op", "_open", name)
end

function manager.doors.close_door(name)
	manager.doors.act_door_name("cl", "_close", name)
end

function manager.doors.pick_door(name)
	manager.doors.act_door_name("pi", "_pick", name)
end

function manager.doors.lock_door(name)
	manager.doors.act_door_name("lock", "_lock", name)
end

function manager.doors.unlock_door(name)
	manager.doors.act_door_name("unl", "_unlock", name)
end
