Script.script:
function manager.actions.wielding.add_loadout(name, right_hand, left_hand, style) local mydb = db:get_database("char_info")
local r, err = db:add(mydb.loadout, { name = name, left_hand = left_hand, right_hand = right_hand, style = style, main_action = nil, fast_action = nil, slow_action = nil, main_action_alt = nil, fast_action_alt = nil, slow_action_alt = nil })
if not r then echo("# error: " .. err .. "\n") else echo("# loadout: '" .. name .. "' added\n") end end

function manager.actions.wielding.clear_loadouts()
  local mydb = db:get_database("char_info")
  db:delete(mydb.loadout, true)

  echo("# loadouts cleared\n")
end

function manager.actions.wielding.list_loadout_slots()
  local mydb = db:get_database("char_info")
  local char_vars = gw.initialization.get_char_vars(mydb, gw.initialization.character)

  echo(string.format("# loadout slot 1 = %s\n", char_vars.loadout_slot1))
  echo(string.format("# loadout slot 2 = %s\n", char_vars.loadout_slot2))
  echo(string.format("# loadout slot 3 = %s\n", char_vars.loadout_slot3))
end

